import classes from "./Checkout.module.css";
import { useRef, useState } from "react";

const Checkout = (props) => {
  const [formInputValidity, setFormInputValidity] = useState({
    name: true,
    adress: true,
    postal: true,
    city: true,
  });

  const nameInputRef = useRef();
  const adressInputRef = useRef();
  const postalInputRef = useRef();
  const cityInputRef = useRef();
  const confirmHandler = (event) => {
    event.preventDefault();

    const enteredName = nameInputRef.current.value;
    const enteredAdress = adressInputRef.current.value;
    const enteredPostal = postalInputRef.current.value;
    const enteredCity = cityInputRef.current.value;

    setFormInputValidity({
      name: !enteredName.trim().length > 5,
      adress: !enteredAdress.trim().length > 10,
      postal: !enteredPostal.trim().length === 4,
      city:  !enteredCity.trim().length > 5,
    });

    if (
      !enteredName.trim().length > 5 &&
      !enteredAdress.trim().length > 10 &&
      !enteredPostal.trim().length === 4 &&
      !enteredCity.trim().length > 5
    ) {
      return;
    }
  };

  return (
    <form className={classes.form} onSubmit={confirmHandler}>
      <div className={classes.control}>
        <label htmlFor="name">Full Name</label>
        <input type="text" id="name" ref={nameInputRef} />
        {formInputValidity.name && <p>Please enter a valid name!</p>}
      </div>
      <div className={classes.control}>
        <label htmlFor="adress">Adress</label>
        <input type="text" id="adress" ref={adressInputRef} />
        {!formInputValidity.adress && <p>Please enter a valid adress!</p>}

      </div>
      <div className={classes.control}>
        <label htmlFor="postal">Postal Code</label>
        <input type="text" id="postal" ref={postalInputRef} />
        {!formInputValidity.postal && <p>Please enter a valid postal code!</p>}

      </div>
      <div className={classes.control}>
        <label htmlFor="city">City</label>
        <input type="text" id="city" ref={cityInputRef} />
      </div>
      {!formInputValidity.city && <p>Please enter a valid city!</p>}

      <div className={classes.actions}>
        <button type="button" onClick={props.onCancel}>
          Cancel
        </button>
        <button className={classes.submit}>Confirm</button>
      </div>
    </form>
  );
};

export default Checkout;
